{
    Assembly script = await CSScript.Evaluator
                                    .CompileCodeAsync(
                                                 @"using System;
                                                   public class Calc
                                                   {
                                                       public int Sum(int a, int b)
                                                       {
                                                           return a+b;
                                                       }
                                                   }");
    dynamic calc = script.CreateObject("*");

    textBox1.Text = calc.Sum(3, 2).ToString();
}

